export function getTechIcon(techName: string) {
  const normalizedName = techName.toLowerCase().replace(/\s+/g, "")

  if (normalizedName.includes("python")) return TechIcons.Python
  if (normalizedName === "c" || normalizedName === "clang") return TechIcons.C
  if (normalizedName.includes("c++") || normalizedName.includes("cpp")) return TechIcons["C++"]
  if (normalizedName.includes("java") && !normalizedName.includes("javascript")) return TechIcons.Java
  if (normalizedName.includes("html")) return TechIcons.HTML5
  if (normalizedName.includes("css")) return TechIcons.CSS3
  if (normalizedName.includes("bootstrap")) return TechIcons.Bootstrap
  if (normalizedName.includes("mysql")) return TechIcons.MySQL
  if (normalizedName.includes("firebase")) return TechIcons.Firebase
  if (normalizedName.includes("dsa") || normalizedName.includes("datastructures")) return TechIcons.DSA
  if (normalizedName.includes("oop") || normalizedName.includes("objectoriented")) return TechIcons.OOP
  if (normalizedName.includes("github")) return TechIcons.GitHub
  if (normalizedName.includes("vscode") || normalizedName.includes("visualstudio")) return TechIcons["VS Code"]
  if (normalizedName.includes("colab") || normalizedName.includes("googlecolab")) return TechIcons["Google Colab"]
  if (normalizedName.includes("aws") || normalizedName.includes("amazon")) return TechIcons.AWS

  if (normalizedName.includes("javascript") || normalizedName.includes("js")) return TechIcons.JavaScript
  if (normalizedName.includes("typescript") || normalizedName.includes("ts")) return TechIcons.TypeScript
  if (normalizedName.includes("tailwind")) return TechIcons.Tailwind
  if (normalizedName.includes("scss") || normalizedName.includes("sass")) return TechIcons.SCSS
  if (normalizedName.includes("framer")) return TechIcons.Framer
  if (normalizedName.includes("react")) return TechIcons.React
  if (normalizedName.includes("redux")) return TechIcons.Redux
  if (normalizedName.includes("next")) return TechIcons.Nextjs
  if (normalizedName.includes("nodejs") || normalizedName.includes("node")) return TechIcons.Nodejs
  if (normalizedName.includes("express")) return TechIcons.Express
  if (normalizedName.includes("mongo")) return TechIcons.MongoDB
  if (normalizedName.includes("supabase")) return TechIcons.Supabase
  if (normalizedName.includes("sanity")) return TechIcons.Sanity
  if (normalizedName.includes("docker")) return TechIcons.Docker
  if (normalizedName.includes("vercel")) return TechIcons.Vercel
  if (normalizedName.includes("git") && !normalizedName.includes("github")) return TechIcons.Git
  if (normalizedName.includes("postman")) return TechIcons.Postman
  if (normalizedName.includes("cloudflare")) return TechIcons.Cloudflare

  return null
}
